public class Literals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Decimal Literal");
		int a = 45; //Decimal Literal
		System.out.println(a);
		
		System.out.println("Octal literal");
		int b = 045; //Octal literal 0 is a prefix
		System.out.println(b);
		
		System.out.println("Hexadecimal Literal");
		int c = 0x45; //Hexadecimal Literal 0x is a prefix
		System.out.println(c);
		
		System.out.println("Binary Literal");
		int d = 0b100101; // Binary Literal 0b is a prefix
		System.out.println(d);
	}

}
