public class Truncation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Division using integer datatype"); 
		int a = 25;
		int b = 2;
		int c = a/b;
		System.out.println(c); 
		
		System.out.println("Division using real number datatype"); 
		float d = 25;
		float e = 2;
		float f = d/e;
		System.out.println(f);
	}

}
