
public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Write a Program to Print 
		/*
		 ****
		 ****
		 ****
		 ****
		 */
		
		for(int i = 0; i < 4 ; i++ )
		{
			for(int j = 0 ; j < 4; j++)
			{
				System.out.print("*");
			}
			System.out.println();
		}
		
	}

}
